INSERT INTO Manga.Manga_Detail ( GUID, ID, PIC_NAME, PIC_PATH, MAIN_GUID ) SELECT t.GUID, MANGA.SEQ_MANGADETAIL_ID.NEXTVAL, t.PIC_NAME, PIC_PATH, MAIN_GUID FROM ( SELECT * FROM(
SELECT sys_guid() AS GUID, #{mangaDetail.picName,jdbcType=VARCHAR} AS PIC_NAME, #{mangaDetail.picPath,jdbcType=CLOB} AS PIC_PATH, #{mangaDetail.mainGuid,jdbcType=VARCHAR} AS MAIN_GUID FROM dual
) a ORDER BY a.PIC_NAME ASC ) t
